window.setDeadzone=function(v){
//Anythingsmallerthanthisisassumedtobe0,0
varDEADZONE=0.2;

if(Math.abs(v)<DEADZONE){
//Inthedeadzone,setto0
v=0;

if(v==null){
return0;
}else{
returnv;
}

}else{
//We'reoutsidethedeadzone,butwe'dliketosmooth
//thisvalueoutsoitstillrunsnicelybetween0..1.
//Thatis,wedon'twantittojumpsuddenlyfrom0to
//DEADZONE.

//Remapvfrom
//DEADZONE..1to0..(1-DEADZONE)
//orfrom
//-1..-DEADZONEto-(1-DEADZONE)..0

v=v-Math.sign(v)*DEADZONE;

//Remapvfrom
//0..(1-DEADZONE)to0..1
//orfrom
//-(1-DEADZONE)..0to-1..0

v/=(1.0-DEADZONE);

if(v==null){
return0;
}else{
returnv;
}
}
};

